import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "./Card";
import styled from "styled-components";
import PropTypes from "prop-types";

function RecipesCard({ className }) {
  const [recipeCardData, setrecipeCardData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/recipes/`)
      .then((response) => {
        setrecipeCardData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className={className}>
      <div className="recipes_cards" id="scoll">
        {recipeCardData.map((recipe, index) => (
          <Card key={index} recipeData={recipe} />
        ))}
      </div>
    </div>
  );
}

RecipesCard.propTypes = {
  className: PropTypes.string.isRequired,
};

export default styled(RecipesCard)`
  .recipes_cards {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-start;
    margin-left: 10px;
    margin-top: 50px;
  }
  .recipes_cards .card {
    background-color: rgb(255, 255, 255);
    width: 200px;
    height: 300px;
    margin: 10px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: #e23c34;
  }

  .recipes_cards .card .recipe_img {
    width: 180px;
    height: 180px;
    display: flex;
    overflow: hidden;
    justify-content: center;
  }

  .recipes_cards .card .recipe_img img {
    max-width: 100%;
    max-height: 100%;
    width: auto;
    height: auto;
    object-fit: cover;
  }
  .recipes_cards .card p {
    padding-top: 20px;
  }

  .recipes_cards .card button {
    background-color: #e23c34;
    border: 1px solid #e23c34;
    padding: 5px;
    margin: 10px;
    border-radius: 30px;
    width: 120px;
    color: #fff;
    transition: 0.3s ease, color 0.3s ease;
    cursor: pointer;
    font-size: medium;
  }
  .recipes_cards .card button:hover {
    background-color: #fdee82;
    border: 1px solid #e23c34;
    color: #e23c34;
  }
`;
