import React, { useState } from "react";

function LevelSelector() {
  const [level, setlevel] = useState("");

  const levelClick = (event) => {
    const level = event.target.value;
    setlevel(level);
    // add API call
  };

  return (
    <div className="level">
      <h2>Level</h2>
      <div className="box">
        <form action="/">
          <div>
            <label htmlFor="easy">Easy</label>
            <input
              type="radio"
              id="easy"
              name="level"
              value="easy"
              checked={level === "easy"}
              onChange={levelClick}
            />
          </div>
          <div>
          <label htmlFor="medium">Medium</label>
          <input
            type="radio"
            id="medium"
            name="level"
            value="medium"
            checked={level === "medium"}
            onChange={levelClick}
          /></div>
<div></div>
          <label htmlFor="hard">Hard</label>
          <input
            type="radio"
            id="hard"
            name="level"
            value="hard"
            checked={level === "hard"}
            onChange={levelClick}
          />
        </form>
      </div>
    </div>
  );
}

export default LevelSelector;
