import React, { useRef, useEffect, useState } from "react";
import RecipesCard from "./cardlist/RecipesCard";
import Banner from "./Banner";
import TimeSlider from "./TimeSlider";
import LevelSelector from "./LevelSelector";
import styled from "styled-components";
import PropTypes from "prop-types";
import axios from "axios";

function Home({ className }) {
  const [data, setData] = useState([]);
  const [selectType, setSelectType] = useState(null);
  const [selectOptions, setSelectOptions] = useState(null);
  const baseURL = "http://127.0.0.1:8000/recipes";

  const [recipeCardData, setRecipeCardData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const sliderRef = useRef(null);
  const outputRef = useRef(null);
  const [sliderValue, setSliderValue] = useState(30);

  const [level, setLevel] = useState("");

  useEffect(() => {
    axios
      .get(`http://localhost:8000/recipes/`)
      .then((response) => {
        setRecipeCardData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    if (selectType) {
      const apiURL = `${baseURL}?type=${selectType}`;
      axios
        .get(apiURL)
        .then((response) => {
          setData(response.data);
        })
        .catch((error) => {
          console.error("Error fetching data:", error);
        });
    }
  }, [selectType]);

  useEffect(() => {
    if (selectOptions) {
      const apiURL = `${baseURL}?options=${selectOptions}`;
      axios
        .get(apiURL)
        .then((response) => {
          setData(response.data);
        })
        .catch((error) => {
          console.error("Error fetching data:", error);
        });
    }
  }, [selectOptions]);

  const handleType = (dishType) => {
    setSelectType(dishType);
  };

  const handleOptions = (dishOptions) => {
    setSelectOptions(dishOptions);
  };

  // time

  useEffect(() => {
    const slider = sliderRef.current;
    const output = outputRef.current;

    if (slider && output) {
      slider.addEventListener("input", handleSliderChange);
      return () => slider.removeEventListener("input", handleSliderChange);
    }
  }, []);

  useEffect(() => {
    const apiURL = `${baseURL}?total=${sliderValue}`;
    axios
      .get(apiURL)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [sliderValue]);

  const handleSliderChange = () => {
    const slider = sliderRef.current;
    const output = outputRef.current;

    if (slider && output) {
      var value =
        ((slider.value - slider.min) / (slider.max - slider.min)) * 100;
      var color =
        "linear-gradient(to right, #e23c34 0%, #e23c34 " +
        value +
        "%, #d3d3d3 " +
        value +
        "%, #d3d3d3 100%)";
      slider.style.background = color;
      output.innerHTML = slider.value;
      setSliderValue(slider.value);
    }
  };

  // time

  //

  return (
    <div className={className}>
      <Banner />
      {/* ower names */}
      <div class="creator">
        <div class="cards">
          <div class="owner">
            <div class="ownerimg">
              <img src="../images/owner/KwanJai.jpg" alt="img" />
            </div>
            <div class="ownerdetails">
              <h3>Kittipat Saiud</h3>
              <p>652110270</p>
            </div>
          </div>
          <div class="owner">
            <div class="ownerimg">
