import React, { useState } from "react";
import Axios from "axios";
import styled from "styled-components";
import PropTypes from "prop-types";

function Add({ className }) {
  const [ingredients, setIngredients] = useState(["", "", ""]);
  const [steps, setSteps] = useState(["", "", ""]);
  const [isSuccess, setIsSuccess] = useState(false);

  // Function to add a new ingredient field
  const handleAddIngredient = () => {
    setIngredients([...ingredients, ""]);
  };

  // Function to remove a ingredient
  const handleRemoveIngredient = () => {
    const newIngredients = [...ingredients];
    newIngredients.splice(newIngredients.length - 1, 1);
    setIngredients(newIngredients);
  };

  // Function to add a new step
  const handleAddStep = () => {
    setSteps([...steps, ""]);
  };

  // Function to remove a step
  const handleRemoveStep = () => {
    const newSteps = [...steps];
    newSteps.splice(newSteps.length - 1, 1);
    setSteps(newSteps);
  };

  const [formData, setFormData] = useState({
    title: "",
    image: [""],
    video: "",
    ingredients: [],
    steps: [],
    prepTime: "",
    cookTime: "",
    level: "easy",
    serving: "",
    type: "breakfast",
    options: "nonspicy",
    author: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "image1" || name === "image2") {
      // Handle image inputs
      const imageIndex = name === "image1" ? 0 : 1;
      const newImages = [...formData.image];
      newImages[imageIndex] = value;
      setFormData({
        ...formData,
        image: newImages,
      });
    } else if (name === "author" || name === "serving" || name === "options") {
      // Handle author, serving, and options inputs
      setFormData({
        ...formData,
        [name]: value,
      });
    } else if (name.startsWith("ingredient")) {
      // Handle ingredients inputs
      const ingredientIndex = parseInt(name.match(/\d+/)[0]);
      const newIngredients = [...formData.ingredients];
      newIngredients[ingredientIndex] = value;
      setFormData({
        ...formData,
        ingredients: newIngredients,
      });
    } else if (name.startsWith("step")) {
      // Handle steps inputs
      const stepIndex = parseInt(name.match(/\d+/)[0]);
      const newSteps = [...formData.steps];
      newSteps[stepIndex] = value;
      setFormData({
        ...formData,
        steps: newSteps,
      });
    } else {
      // Handle other inputs
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate required fields
    const requiredFields = [
      "title",
      "prepTime",
      "cookTime",
      "level",
      "serving",
      "type",
      "options",
      "author",
    ];

    const emptyFields = requiredFields.filter(
      (field) => formData[field].trim() === ""
    );

    if (emptyFields.length > 0) {
      alert(`Please fill in all required fields: ${emptyFields.join(", ")}`);
      return;
    }
    // Validate Prep Time, Cook Time, and Servings
    const prepTimeValue = parseFloat(formData.prepTime);
    const cookTimeValue = parseFloat(formData.cookTime);
    const servingValue = parseFloat(formData.serving);

    if (isNaN(prepTimeValue) || isNaN(cookTimeValue) || isNaN(servingValue)) {
      alert("Prep Time, Cook Time, and Servings must be numbers.");
      return; // Do not proceed with the submission
    }

    try {
      const response = await Axios.post(
        "http://localhost:8000/recipes",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.status === 200) {
        throw new Error(
          `Network response was not ok: ${response.status} - ${response.statusText}`
        );
      }

      const data = response.data;
      console.log("Recipe saved:", data);
      setIsSuccess(true);
    } catch (error) {
      console.error("Error saving recipe:", error);
    }
  };

  return (
    <div className={className}>
      <form onSubmit={handleSubmit}>
        <div className="container">
          <div className="top_name">
            <h1>Add a Recipe</h1>
            <p>
              Uploading personal recipes is easy! Add yours to your favorites,
              share with friends, family, or the Allrecipes community.
            </p>
          </div>

          <div className="section_1">
            <div>
              <div className="title">
                <h2>Recipe Title</h2>
                <div className="typetitle">
                  <input
                    type="text"
                    name="title"
                    placeholder="Give your recipe a title"
                    value={formData.title}
                    onChange={handleChange}
                  />
                </div>
              </div>
            </div>
            <div>
              <div className="photo">
                <h2>Photo (optional)</h2>
                <div className="typephoto1">
                  <input
                    type="text"
                    name="image1"
                    placeholder="Add image url"
                    value={formData.image[0]} // Update the value to use an array
                    onChange={handleChange}
                  />
                </div>
                <div className="typephoto">
                  <input
                    type="text"
                    name="image2"
                    placeholder="Add image url"
                    value={formData.image[1]} // Update the value to use an array
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="video">
                <h2>Video (optional)</h2>
                <div className="typevideo">
                  <input
                    type="text"
                    name="video"
                    placeholder="Add video url"
                    value={formData.video}
                    onChange={handleChange}
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="section_2">
            <h2>Ingredients</h2>
            <p>
              Enter one ingredient per line. Include the quantity and any
              special preparation. Use optional headers to organize the
              different parts of the recipe.
            </p>
            <h3>Enter ingredients below</h3>
            {ingredients.map((ingredient, index) => (
              <div key={index} className="typeingre">
                <input
                  type="text"
                  placeholder="e.g. 2 cups flour"
                  value={formData.ingredients[index]}
                  onChange={(e) => {
                    const newIngredients = [...formData.ingredients];
                    newIngredients[index] = e.target.value;
                    setFormData({
                      ...formData,
                      ingredients: newIngredients,
                    });
                  }}
                />
                <i
                  onClick={handleRemoveIngredient}
                  className="fa-solid fa-circle-minus"
                ></i>
              </div>
            ))}

            <button type="button" onClick={handleAddIngredient}>
              +Add Ingredient
            </button>
          </div>

          <div className="section_3">
            <h2>Directions</h2>
            <p>
              Explain how to make your recipe, including oven temperatures,
              baking or cooking times, and pan sizes. Organize the different
              parts of the recipe
            </p>
            <h3>Enter directions below</h3>
            {steps.map((step, index) => (
              <div key={index} className="typedirec">
                <p>Step {index + 1}</p>
                <textarea
                  value={formData.steps[index]}
                  onChange={(e) => {
                    const newSteps = [...formData.steps];
                    newSteps[index] = e.target.value;
                    setFormData({
                      ...formData,
                      steps: newSteps,
                    });
                  }}
                  placeholder="e.g. Reheat oven at 30 degrees F..."
                ></textarea>
                <i
                  onClick={handleRemoveStep}
                  className="fa-solid fa-circle-minus"
                ></i>
              </div>
            ))}
            <button type="button" onClick={handleAddStep}>
              +Add Step
            </button>
          </div>

          <div className="section_4">
            <div>
              <div className="selectBox">
                <h2>Prep Time</h2>
                <input
                  type="text"
                  name="prepTime"
                  placeholder="0"
                  value={formData.prepTime}
                  onChange={handleChange}
                />
                <label for="preptime">min</label>
              </div>
              <div className="selectBox">
                <h2>Cook Time</h2>
                <input
                  type="text"
                  name="cookTime"
                  placeholder="0"
                  value={formData.cookTime}
                  onChange={handleChange}
                />
                <label for="cooktime">min</label>
              </div>
            </div>

            <div>
              <div className="selectBox">
                <label for="level">
                  <h2>Level</h2>
                </label>
                <select id="level" name="level">
                  <option value="easy">Easy</option>
                  <option value="Medium">Medium</option>
                  <option value="hard">Hard</option>
                </select>
              </div>
              <div className="selectBox">
                <label for="recipe-type">
                  <h2>Type</h2>
                </label>

                <select
                  id="recipe-type"
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  <option value="breakfast">Breakfast</option>
                  <option value="lunch">Lunch</option>
                  <option value="dinner">Dinner</option>
                  <option value="appetizer">Appetizer</option>
                  <option value="salad">Salad</option>
                  <option value="main-course">Main Course</option>
                  <option value="baked-goods">Baked Goods</option>
                  <option value="dessert">Dessert</option>
                  <option value="soup">Soup</option>
                </select>
              </div>
            </div>

            <div>
              <div className="selectBox">
                <h2>Servings</h2>
                <input
                  type="text"
                  name="serving"
                  placeholder="0"
                  value={formData.serving}
                  onChange={handleChange}
                />
              </div>
              <div className="selectBox">
                <label for="recipe-options">
                  <h2>Options</h2>
                </label>
                <select
                  id="recipe-options"
                  name="opt"
                  value={formData.options}
                  onChange={handleChange}
                >
                  <option value="nonspicy">Non-Spicy</option>
                  <option value="spicy">Spicy</option>
                  <option value="vegetarian">Vegetarian</option>
                  <option value="vegan">Vegan</option>
                </select>
              </div>
            </div>
          </div>

          <div className="section_5">
            <h2>Author Name</h2>
            <input
              type="text"
              name="author"
              placeholder="Your name"
              value={formData.author}
              onChange={handleChange}
            />
          </div>

          <div className="section_6">
            <button className="cancel-button" type="button">
              Cancel {/* Use type="reset" to reset the form */}
            </button>
            <button className="save-button" type="submit">
              Save Recipe
            </button>
          </div>
        </div>
      </form>
      {isSuccess && (
        <div className="success-message">Recipe saved successfully!</div>
      )}
    </div>
  );
}

Add.propTypes = {
  className: PropTypes.string.isRequired,
